function isRefreshAllowed(){const now=Date.now();const lastRefresh=localStorage.getItem("lastRefreshTime");const tenMinutesInMs=10000;if(!lastRefresh){return true}if(now-lastRefresh>tenMinutesInMs){return true}return false}function handleRefresh(){if(isRefreshAllowed()){localStorage.setItem("lastRefreshTime",Date.now());window.location.reload()}}window.addEventListener("beforeunload",function(event){if(!isRefreshAllowed()){event.preventDefault();return""}else{localStorage.setItem("lastRefreshTime",Date.now())}});